import { Component, OnInit } from '@angular/core';
import { EventoService } from './evento.servicio';

interface Evento {
  Id: number;
  IdAdministrador: number;
  Nombre: string;
  Descripcion: string;
  Ubicacion: string;
  InformacionContacto: string;
  FechaInicio: Date;
  FechaFin: Date;
  Tipo: boolean;
  Estado: boolean;
  FechaCreacion: Date;
  Certificados: boolean;
}

@Component({
  selector: 'app-evento',
  templateUrl: './evento.component.html',
  styleUrls: ['./evento.component.css']
})
export class EventoComponent implements OnInit {
  eventos: Evento[] = [];
  selectedEvento?: Evento;
  name: string = "";
  constructor(private eventoService: EventoService) { }

  ngOnInit(): void {
    this.loadEventos();
  }

  loadEventos(): void {
    this.eventoService.getEventosDisponibles().subscribe((data: Evento[]) => {
      this.eventos = data;
    });
  }

  selectEvento(evento: Evento): void {
    this.selectedEvento = evento;
  }

  BuscarName(name: string): void {
    this.eventoService.getDetalleEventoName(name).subscribe((data: Evento) => {
      this.selectedEvento = data;
    });
  }

  BuscarId(id: number): void {
    this.eventoService.getDetalleEventoId(id).subscribe((data: Evento) => {
      this.selectedEvento = data;
    });
  }
}
