import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

interface Evento {
  Id: number;
  IdAdministrador: number;
  Nombre: string;
  Descripcion: string;
  Ubicacion: string;
  InformacionContacto: string;
  FechaInicio: Date;
  FechaFin: Date;
  Tipo: boolean;
  Estado: boolean;
  FechaCreacion: Date;
  Certificados: boolean;
}

@Injectable({
  providedIn: 'root'
})
export class EventoService {
  private baseUrl = 'http://localhost:5000/GestionEvento'; // Cambia esto a la URL de tu API

  constructor(private http: HttpClient) { }

  getEventosDisponibles(): Observable<Evento[]> {
    return this.http.get<Evento[]>(`${this.baseUrl}/evento-disponible`);
  }

  getDetalleEventoId(id_evento: number): Observable<Evento> {
    return this.http.get<Evento>(`${this.baseUrl}/detalle-evento-id`, { params: { id_evento } });
  }

  getDetalleEventoName(name_evento: string): Observable<Evento> {
    return this.http.get<Evento>(`${this.baseUrl}/detalle-evento-name`, { params: { name_evento } });
  }
}
